import React from 'react';
import {Redirect} from 'react-router';
import axios from 'axios';


class Register extends React.Component {
   constructor() {
       super()
       this.state = {
           registerError: false,
           username: '',
           password: '',
           latitude: '',
           longitude: '',
           photo: '',
           food: '',
           gender: '',
           redirect: false,
       }
       this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleLocationChange = this.handleLocationChange.bind(this);

   }

   handleLocationChange(e){
       var location = e.target.value;
       axios.get('https://maps.googleapis.com/maps/api/geocode/json', {
                           headers: {
                                'Content-Type': 'application/json',
                                // 'Content-Type': 'application/x-www-form-urlencoded',
                            },
                            params: {
                                address: location,
                                key: 'AIzaSyD_VlYXiXK8cp9x_t1LL6vAU5a4KP9gt3g'
                            }
                        })
                        .then(function(response) {
                            console.log(response);

                            console.log(response.data.results[0].geometry.location.lat);
                            this.setState({
                                latitude: response.data.results[0].geometry.location.lat,
                                longitude: response.data.results[0].geometry.location.lng,
                            })
                        })
                        .catch(function(error) {
                            console.log(error);
                        });
    }

   handleChange(e){
        this.setState({
            [e.target.name] : e.target.value,
        })
    }

   handleSubmit(e,v) {
       e.preventDefault();
       fetch('/register', {
           method: 'POST',
           headers: {
                'Content-Type': 'application/json',
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password,
                latitude: this.state.latitude,
                longitude: this.state.longitude,
                photo: this.state.photo,
                food: this.state.food,
                gender: this.state.gender,
                age: this.state.age,
            })
       })
       .then(response => response.json())
       .then(json => {
           console.log('register success', json);
           this.setState({redirect: true})
       })
       .catch(err => this.setState({registerError: true}))
   }




    render(){
          if (this.state.redirect) return <Redirect to="/login" />;
        return (
             <html>
            <head>
                <meta charset="UTF-8"/>
                <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossorigin="anonymous"/>
                <link rel="stylesheet" type="text/scss" href="style.scss"/>
                <title>Registration Form</title>
            </head>
            <body>
                <h1>Registration</h1>
                <br/>
                <div className="container">
                <form onSubmit={this.handleSubmit}>
                    Enter username <input name="username" className="form-control" onChange={this.handleChange}/><br/>
                    Enter password <input type="password" name="password" className="form-control" onChange={this.handleChange}/><br/>
                    Enter location <input name="location" className="form-control" onChange={this.handleLocationChange}/><br/>
                    Latitude: <input value={this.state.latitude} />
                    Longitude: <input value={this.state.longitude} /><br/><br/>
                    Enter photo <input name="photo" className="form-control" onChange={this.handleChange}/><br/>
                    Enter food <input name="food" className="form-control" onChange={this.handleChange}/><br/><br/>
                    Enter gender <input name="gender" className="form-control" onChange={this.handleChange}/><br/><br/>
                    Enter age <input name="age" className="form-control" onChange={this.handleChange}/><br/><br/>
                    <input id="createAcc" type="submit" className="btn btn-primary" value="Create Account"/>
                </form>
                </div>
            </body>
        </html>);
    }
}

export default Register;
