import React from 'react';
import Navbar from '../navbar/navbar';
import Layout from '../layout/layout';
import Cookies from 'js-cookie';
import styles from './style.scss';
import Modal from '../modal/modal'
import Row from '../row/row';


class Search extends React.Component {
    constructor(){
        super();
        this.state = {
            users: [],
            show: false,
        }
        this.calculateDistance = this.calculateDistance.bind(this);
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

    }

    componentDidMount() {
        fetch('/users')
        .then(response => response.json())
        .then(json => this.setState({users: json.queryRes}))
        .catch(err => console.log(err))
    }

  render() {
      console.log('Login state', this.state.users);
      const users = this.state.users.map( (user,index) => {
          console.log("INSIDE MAP",user.photo);
          return   <Row user={user} />
      })
    return (
        <Layout>
            <Navbar />
              <h3>Search Page</h3>
              <Modal show={this.state.show} handleClose={this.hideModal}>
                          <p>Modal</p>
                          <p>Data</p>
                        </Modal>
              <div class="tableDiv col-9">
              <table class="table table-striped">
                <thead>
                    <tr>
                        <td scope="col">ID</td>
                        <td scope="col">Name</td>
                        <td scope="col">Age</td>
                        <td scope="col">Gender</td>
                        <td scope="col">Food Likes</td>
                        <td scope="col">Location</td>
                        <td scope="col">Distance from you</td>
                        <td scope="col">View Profile</td>
                    </tr>
                </thead>
            <tbody>
                {users}
            </tbody>
            </table>
            </div>
         </Layout>
    );
  }
}

export default Search;
