import React from 'react';
import Navbar from '../navbar/navbar';
import Layout from '../layout/layout';
import Cookies from 'js-cookie';
import styles from './style.scss';



class Search extends React.Component {
    constructor(){
        super();
        this.state = {
            users: []
        }

    }

    componentDidMount() {
        fetch('/users')
        .then(response => response.json())
        .then(json => this.setState({users: json.queryRes}))
        .catch(err => console.log(err))
    }

    calculateDistance(lat1, lon1, lat2, lon2) {
        console.log(lat1); //1.274250
        console.log(lon1); //103.845410
        console.log(lat2);
        console.log(lon2);

        var R = 6371; // km (change this constant to get miles)
        var dLat = (lat2-lat1) * Math.PI / 180;
        var dLon = (lon2-lon1) * Math.PI / 180;
        var a = Math.sin(dLat/2) * Math.sin(dLat/2) +
        Math.cos(lat1 * Math.PI / 180 ) * Math.cos(lat2 * Math.PI / 180 ) *
        Math.sin(dLon/2) * Math.sin(dLon/2);
        var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
        var d = R * c;

            return Math.round(d*1000)+" m";
    }

    toggleModal() {

    }

    popModal() {
        var modal = document.querySelector(".modal");
        var trigger = document.querySelector(".trigger");
        var closeButton = document.querySelector(".close-button");

        this.toggleModal();
        modal.classList.toggle("show-modal");
    }


  render() {
      console.log('Login state', this.state.users);
      const users = this.state.users.map((user,index) => {
          return <tr>
                    <td>{index+1}</td>
                    <td>{user.username}</td>
                    <td>{user.age}</td>
                    <td>{user.gender}</td>
                    <td>{user.food}</td>
                    <td>{user.location}</td>
                    <td>{this.calculateDistance(this.props.userData.latitude, this.props.userData.longitude, user.latitude, user.longitude)}</td>
                    <td>
                        <button onClick={this.popModal} class="trigger">Click here to view profile!</button>
                        <div class="modal">
                            <div class="modal-content">
                                <span class="close-button">&times;</span>
                                <h1>Hello, I am a modal!</h1>
                            </div>
                        </div>
                    </td>
                </tr>
      })
    return (
        <Layout>
            <Navbar />
              <h3>Search Page</h3>
              <div class="tableDiv col-9">
              <table class="table table-striped">
                <thead>
                    <tr>
                        <td scope="col">ID</td>
                        <td scope="col">Name</td>
                        <td scope="col">Age</td>
                        <td scope="col">Gender</td>
                        <td scope="col">Food Likes</td>
                        <td scope="col">Location</td>
                        <td scope="col">Distance from you</td>
                        <td scope="col">View Profile</td>
                    </tr>
                </thead>
            <tbody>
                {users}
            </tbody>
            </table>
            </div>
         </Layout>
    );
  }
}

export default Search;
