import React from 'react';
import Navbar from '../navbar/navbar';
import Cookies from 'js-cookie';
import styles from './style.scss';
import Modal from '../modal/modal'


class Row extends React.Component {
    constructor(){
        super();
        this.state = {
            users: [],
            show: false,
        }
        this.calculateDistance = this.calculateDistance.bind(this);
        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

    }

    calculateDistance(lat1, lon1, lat2, lon2) {
        console.log(lat1); //1.274250
        console.log(lon1); //103.845410
        console.log(lat2);
        console.log(lon2);

        var R = 6371; // km (change this constant to get miles)
        var dLat = (lat2-lat1) * Math.PI / 180;
        var dLon = (lon2-lon1) * Math.PI / 180;
        var a = Math.sin(dLat/2) * Math.sin(dLat/2) +
        Math.cos(lat1 * Math.PI / 180 ) * Math.cos(lat2 * Math.PI / 180 ) *
        Math.sin(dLon/2) * Math.sin(dLon/2);
        var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
        var d = R * c;

            return Math.round(d*1000)+" m";
    }

   showModal() {
       this.setState({ show: true });
   }

    hideModal() {
        this.setState({ show: false });
    };



  render() {
      console.log('Login state', this.state.users);
      const users = this.state.users.map( (user,index) => {
          console.log("INSIDE MAP",user.photo);
          return
              <tr>
                    <td>{index+1}</td>
                    <td>{user.username}</td>
                    <td>{user.age}</td>
                    <td>{user.gender}</td>
                    <td>{user.food}</td>
                    <td>{user.location}</td>
                    <td>{this.calculateDistance(this.props.userData.latitude, this.props.userData.longitude, user.latitude, user.longitude)}</td>
                    <td>
                        <Modal show={this.state.show} handleClose={this.hideModal} userPhoto={user.photo}    >
                        </Modal>
                        <button onClick={this.showModal}>Click here to view photo!</button>
                    </td>
                </tr>
      })
  }
}

export default Row;
