import React from 'react';
import {Redirect} from 'react-router';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';


class Register extends React.Component {
   constructor() {
       super()
       this.state = {
           registerError: false,
           username: '',
           password: '',
           location: '',
           photo: '',
           food: '',
           gender: '',
           redirect: false,
       }
       this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

   }


   handleChange(e){
        this.setState({
            [e.target.name] : e.target.value,
        })
    }

   handleSubmit(e,v) {
       e.preventDefault();
       fetch('/register', {
           method: 'POST',
           headers: {
                'Content-Type': 'application/json',
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password,
                location: this.state.location,
                photo: this.state.photo,
                food: this.state.food,
                gender: this.state.gender,
                age: this.state.age,
            })
       })
       .then(response => response.json())
       .then(json => {
           console.log('register success', json);
           this.setState({redirect: true})
       })
       .catch(err => this.setState({registerError: true}))
   }



    render(){
          if (this.state.redirect) return <Redirect to="/login" />;
        return (
             <html>
            <head>
                <meta charset="UTF-8"/>
                <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossorigin="anonymous"/>
                <link rel="stylesheet" type="text/scss" href="style.scss"/>
                <title>Registration Form</title>
            </head>
            <body>
                <h1>Registration</h1>
                <br/>
                <div className="container">
                <form onSubmit={this.handleSubmit}>
                    Enter username <input name="username" className="form-control" onChange={this.handleChange}/><br/>
                    Enter password <input type="password" name="password" className="form-control" onChange={this.handleChange}/><br/>
                    Enter location <input name="location" className="form-control" onChange={this.handleChange}/><br/>
                    Enter photo <input name="photo" className="form-control" onChange={this.handleChange}/><br/>
                    Enter food <input name="food" className="form-control" onChange={this.handleChange}/><br/><br/>
                    Enter gender <input name="gender" className="form-control" onChange={this.handleChange}/><br/><br/>
                    Enter age <input name="age" className="form-control" onChange={this.handleChange}/><br/><br/>
                    <input id="createAcc" type="submit" className="btn btn-primary" value="Create Account"/>
                </form>
                </div>
                <script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyD_VlYXiXK8cp9x_t1LL6vAU5a4KP9gt3g&libraries=places&callback=initAutocomplete"
         async defer></script>
            </body>
        </html>);
    }
}

export default Register;
