var React = require('react');
import {Redirect} from 'react-router';
class Login extends React.Component {
    constructor(){
        super();
        this.state = {
            loginError: false,
            username: '',
            password: '',
            redirect: false,
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleChange(e){
        this.setState({
            [e.target.name] : e.target.value,
        })
    }

    handleSubmit(e, v){
        e.preventDefault();
        fetch('/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: JSON.stringify({
                username: this.state.username,
                password: this.state.password,
            })
        })
        .then(response => response.json())
        .then(json => {
            this.setState({redirect: true})
        })
        .catch(err => this.setState({loginError: true}))
    }

  render() {
      console.log('Login state', this.state);
      if (redirect) return <Redirect to='/home'/>;
    return (
        <html>
            <head>
                <meta charset="UTF-8"/>
                <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossorigin="anonymous"/>
                <link rel="stylesheet" type="text/css" href="/loginstyle.css"/>
                <title>Login Form</title>
            </head>
            <body>
                <h1>Login</h1>
                <br/>
                <div className="container">
            <form onSubmit={this.handleSubmit}>
                Username: <input className="form-control" name="username" onChange={this.handleChange} /><br/>
                {
                    this.state.loginError===true ?
                    <div className="invalid-feedback" style={{display: 'block'}}>
                        Incorrect username or password
                    </div> :
                    null
                }
                Password: <input type="password" className="form-control" name="password" onChange={this.handleChange}/><br/>
                {
                    this.state.loginError===true ?
                    <div className="invalid-feedback" style={{display: 'block'}}>
                        Incorrect username or password
                    </div> :
                    null
                }
                <input className="btn btn-primary" type="submit" value="Login"/>
            </form>
            <a href="/register">Register New Account</a>
            </div>
        </body>
    </html>
    );
  }
}

export default Login;
